<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Email_templates_model extends Fm_model {    public $table = 'email_templates';    public $id = 'id';    public $order = 'ASC';    function __construct() {        parent::__construct();    }    // get total rows    function total_rows($q = NULL) {                if($q){            $this->db->like('title', $q);            $this->db->or_like('template', $q);            $this->db->or_like('slug', $q);                    $this->db->or_like('adminnotes', $q);        }        $this->db->from($this->table);        return $this->db->count_all_results();    }    // get data with limit and search    function get_limit_data($limit, $start = 0, $q = NULL) {        $this->db->order_by($this->id, $this->order);        if($q){            $this->db->like('title', $q);            $this->db->or_like('template', $q);            $this->db->or_like('slug', $q);                    $this->db->or_like('adminnotes', $q);        }        $this->db->limit($limit, $start);        return $this->db->get($this->table)->result();    }}