s="table-striped">                        <?php foreach ($templates as $template) { ?>                            <tr>                                <td><?php echo ++$start; ?></td>                                <td><p><?php echo $template->title ?><br/>                                                                    <small><em><?php echo $template->adminnotes ?></em></small></p>                                </td>                                <td><?php echo $template->slug ?></td>                                                            <td>                                    <span id="<?= $template->id; ?>" class="preview btn btn-primary btn-xs">                                        <i class="fa fa-search"></i>                                        Preview                                    </span>                                    <?php                                                                        echo anchor(site_url(Backend_URL . 'email_templates/update/' . $template->id), '<i class="fa fa-fw fa-edit"></i> Edit', 'class="btn btn-xs btn-default"');                                    echo getETdeleteButton($template->status, $template->id);                                    ?>                                </td>                            </tr>                        <?php } ?>                    </tbody>                </table>            </div>                    </div>        <div class="box-footer">            <div class="row">                                <div class="col-md-6">                    <span class="btn btn-primary">Total : <?= $total_rows ?></span>                </div>                <div class="col-md-6 text-right">                    <?= $pagination; ?>                </div>                            </div>        </div>    </div></section><div class="modal fade" id="modal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">    <div class="modal-dialog" role="document">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal" aria-label="Close">                    <span aria-hidden="true">&times;</span>                </button>                <h4 class="modal-title">Email Template Preview</h4>            </div>            <div class="modal-body">                <h2 class="no-margin" id="subject"></h2>                <div id="content"></div>            </div>                        <div class="modal-footer" style="text-align:left;">                                <p><b>Remark:</b> <span id="remark"></span></p>            </div>                    </div>    </div></div><script type="text/javascript">    $('.preview').on('click', function(){        var id = $(this).attr('id');        $('#modal').modal({show: 'false'});        $.ajax({            type: 'POST',            data: { id: id },            url: 'admin/email_templates/preview/' + id,            dataType: 'json',            beforeSend: function () {                $('#subject').empty();                $('#content').html('<p class="ajax_processing">Sending...</p>');                $('#remark').empty();            },            success: function (respond) {                $('#subject').html(respond.subject);                $('#content').html(respond.content);                $('#remark').html(respond.remark);                            }        });    });</script>    